mapper.insert:
insert into product
	(
	productseq
	, kindseq
	, prd_name
	, prd_price1
	, prd_price2
	, prd_content
	, prd_savfile
	, prd_ognfile
	, prd_quantity
	, prd_indate
	)
values
	(
	productseq.nextval
	, #{kindseq}
	, #{prd_name}
	, #{prd_price1}
	, #{prd_price2}
	, #{prd_content}
	, sysdate
	, #{prd_ognfile}
	, #{prd_quantity}
	, sysdate
	)

mapper.update:
update
	product
set
	prd_name = #{prd_name}
	, prd_price1 =#{prd_price1}
	, prd_price2 = #{prd_price2}
	, prd_content =#{prd_content}
	, prd_quantity =#{prd_quantity}
	, prd_ognfile =#{prd_ognfile}
where
	productseq = #{productseq}
