mapper.insert:
		INSERT INTO IDINFO
		(
			ID
			,PW
			, name
     		,phone
     		,address
     		,birthday
    		,gender
    		,email
			,TYPE
		)
		VALUES
		(
			#{id}
			,#{pw}
			,#{name}
			,#{phone}
			,#{address}
			,#{birthday}
			,#{gender}
			,#{email}
			,#{type}
		)

mapper.select:
select
	id
from
	idinfo
where
	name=#{name}
and
	phone=#{phone}
and
	email=#{email}

select
	pw
from
	idinfo
where
	id=#{id}
and
	phone=#{phone}
and
	email=#{email}
