mapper.insert:
insert into procedure
	(
	PROCEDURESEQ
	,PCD_NAME
	,PCD_SETTING
	,PCD_TIME
	,PCD_PRICE
	,PCD_ORGFILE
	,PCD_SAVFILE
	)
values
	(
	PROCEDURESEQ.nextval
	, #{pcd_name}
	, #{pcd_setting}
	, #{pcd_time}
	, #{pcd_price}
	, #{pcd_orgfile}
	, #{pcd_savfile}
	)

insert into picture
	(
	PICTURESEQ
	,NAME
	,SETTING
	,HASHTAG
	,CONDITION
	,SAVFILE
	,ORGFILE
	)
values
	(
	PICTURESEQ.nextval
	, #{name}
	, #{setting}
	, #{hashtag} 
	, #{condition}
	, #{savfile}
	, #{orgfile}
	)
